import React, { Component } from 'react';

class LikeButton extends Component {

  constructor(props){
    super(props);
    this.state = {like_count: props.like_count, liked_by_user: false, link_id: props.link_id}
  }

  handleLike(){
    console.log('here')
    let options = {
      link_id: this.state.link_id,
      url: 'http://localhost:3000/likes',
      method: 'POST'
    }

    if(this.state.liked_by_user) {
      options.url = `http://localhost:3000/likes/${this.state.link_id}`
      options.method = 'DELETE'
    }

    this.callLikeEndpoint(options);
  }


  callLikeEndpoint(options){
    const csrf_token = document.querySelector('meta[name="csrf-token"]').getAttribute('content')
    console.log(options)
    fetch(options.url, {
      method: options.method,
      body: JSON.stringify({ link_id: options.link_id }),
      headers: { "Content-Type": 'application/json',
       'X-Requested-With': 'XMLHttpRequest',
       'X-CSRF-Token': csrf_token }
    })
      .then((response) => {
        if(response.ok) return response.json();
      })
      .then((json)=> {
        this.updateLike()
      })
  }

  updateLike(){
    this.setState((prevState)=> {
      if(prevState.liked_by_user){
        return {like_count: (prevState.like_count - 1), liked_by_user: false }
      }else{
        return {like_count: (prevState.like_count + 1), liked_by_user: true }
      }
    })
  }

  render(){
    return(
      <div>
        <p onClick={this.handleLike.bind(this)}>X</p>
        <p>{this.state.like_count} likes</p>
      </div>
      )
  }

}

export default LikeButton;
